        return (
            <div id="bingo-body" className="container-fluid bg-dark">
                <div className="row">
                    <div className="col-xl-4">
                    </div>
                    <div className="col-xl-4">
                        <table id="bingo-card" className="w-75 mx-auto">
                            <thead>
                                <tr>
                                    <th>B</th>
                                    <th>I</th>
                                    <th>N</th>
                                    <th>G</th>
                                    <th>O</th>
                                </tr>
                            </thead>
                            <tbody id="bingo-card-player" className="bg-white">
                                <tr>
                                    <td id="B1" className="js-card-cell" data-min="1" data-max="15"></td>
                                    <td id="I1" className="js-card-cell" data-min="16" data-max="30"></td>
