import React, { Component } from 'react'
import './style.css';

class Game extends Component {


    state = {
        populate: [],
        ballarr: [],
        cardnumarr: []
    };

    constructor() {
        super();

        this.shuffleCard = this.shuffleCard.bind(this)
        this.populateCard = this.populateCard.bind(this)

    }

    componentDidMount() {

        this.shuffleCard()

    }



    isValidNumber = ( num, arr = [] ) => {

        if ( !arr.includes( num ) ) {

            return true;

        } else {

            return false;

        }

    };

    generateRandomNumber = ( max, min = 0, arr = [] ) => {

        const _max = Math.floor( max );
        const _min = Math.ceil( min );
        const _arr = arr;
        // random number between and including range
        const _num = Math.floor( Math.random() * ( _max - _min + 1 ) ) + _min;

        // Make sure random number doesn't already exist in the array
        if ( this.isValidNumber( _num, _arr ) ) {

            _arr.push( _num );

            return _num;

        } else {

            return this.generateRandomNumber( _max, _min, _arr );

        }


    };

    randomBallSelector = () => {

        const _ballCount  = this.state.ballarr.length;
        const _randomBall = this.generateRandomNumber( _ballCount - 1 );

        return this.state.ballarr[ _randomBall ];

    };

    populateCard = () => {

        this.setState({cardnumarr: []});

        for ( let i = 25 - 1; i >= 0; i-- ) {

            let _randomNumber = this.generateRandomNumber( document.getElementsByClassName( 'js-card-cell' )[ i ].dataset.max,
                                                            document.getElementsByClassName( 'js-card-cell' )[ i ].dataset.min,
                                                            this.state.cardnumarr );

            document.getElementsByClassName( 'js-card-cell' )[ i ].classList.remove( 'marked' );
            document.getElementsByClassName( 'js-card-cell' )[ i ].innerHTML = _randomNumber;
            document.getElementsByClassName( 'js-card-cell' )[ i ].id = 'js-card-' + _randomNumber;


        }

    }


    shuffleCard(event) {

        this.populateCard()

    }



    render() {

        return (
            <div id="bingo-body" className="container-fluid bg-dark">
                <div className="row">
                    <div className="col-xl-4">
                    </div>
                    <div className="col-xl-4">
                        <table id="bingo-card" className="w-75 mx-auto">
                            <thead>
                                <tr>
                                    <th>B</th>
                                    <th>I</th>
                                    <th>N</th>
                                    <th>G</th>
                                    <th>O</th>
                                </tr>
                            </thead>
                            <tbody id="bingo-card-player" className="bg-white">
                                <tr>
                                    <td id="B1" className="js-card-cell" data-min="1" data-max="15"></td>
                                    <td id="I1" className="js-card-cell" data-min="16" data-max="30"></td>
                                    <td id="N1" className="js-card-cell" data-min="31" data-max="45"></td>
                                    <td id="G1" className="js-card-cell" data-min="46" data-max="60"></td>
                                    <td id="O1" className="js-card-cell" data-min="61" data-max="75"></td>
                                </tr>
                                <tr>
                                    <td id="B2" className="js-card-cell" data-min="1" data-max="15"></td>
                                    <td id="I2" className="js-card-cell" data-min="16" data-max="30"></td>
                                    <td id="N2" className="js-card-cell" data-min="31" data-max="45"></td>
                                    <td id="G2" className="js-card-cell" data-min="46" data-max="60"></td>
                                    <td id="O2" className="js-card-cell" data-min="61" data-max="75"></td>
                                </tr>
                                <tr>
                                    <td id="B3" className="js-card-cell" data-min="1" data-max="15"></td>
                                    <td id="I3" className="js-card-cell" data-min="16" data-max="30"></td>
                                    <td id="N3" className="js-card-cell marked bg-success">FREE</td>
                                    <td id="G3" className="js-card-cell" data-min="46" data-max="60"></td>
                                    <td id="O3" className="js-card-cell" data-min="61" data-max="75"></td>
                                </tr>
                                <tr>
                                    <td id="B4" className="js-card-cell" data-min="1" data-max="15"></td>
                                    <td id="I4" className="js-card-cell" data-min="16" data-max="30"></td>
                                    <td id="N4" className="js-card-cell" data-min="31" data-max="45"></td>
                                    <td id="G4" className="js-card-cell" data-min="46" data-max="60"></td>
                                    <td id="O4" className="js-card-cell" data-min="61" data-max="75"></td>
                                </tr>
                                <tr>
                                    <td id="B5" className="js-card-cell" data-min="1" data-max="15"></td>
                                    <td id="I5" className="js-card-cell" data-min="16" data-max="30"></td>
                                    <td id="N5" className="js-card-cell" data-min="31" data-max="45"></td>
                                    <td id="G5" className="js-card-cell" data-min="46" data-max="60"></td>
                                    <td id="O5" className="js-card-cell" data-min="61" data-max="75"></td>
                                </tr>
                            </tbody>
                        </table>
                        <button id="shuffle" className="mt-4" onClick={this.shuffleCard}>SHUFFLE</button>
                    </div>
                    <div className="col-xl-4">
                        a
                    </div>
                </div>
            </div>
        );
    }

}

export default Game;
