import React, { Component } from 'react'
import Header from './header';
import io from 'socket.io-client'
// import axios from 'axios';
import './style-admin.css';

var socket = io.connect('http://localhost:4000')
var room = localStorage.room_id
var username = localStorage.name

class Game extends Component {

    state = {
        get_winning_pattern: [],
        BallsArr: [],
    };

    // constructor() {
    //     super();       

    // }

    componentDidMount() {

        socket.emit('joinRoom', { username, room });


        socket.on('roomUsers', ({ room, users }) => {
            console.log(room)
            console.log(users)
            this.setState({ lop: users })
        });

    }

    Setppattern = (event) => {
        event.target.classList.toggle("marked");
        console.log(event.target.getAttribute("data-cell"))
    }

    GetPattern = (e) => {

        let getPattern = document.getElementsByClassName('pattern marked')
        let patternObj = []

        for(let i = 0; getPattern.length > i; i++){

            let data = getPattern[i].getAttribute('data-cell')
            patternObj.push(data)

        }

        this.setState({get_winning_pattern: patternObj})

    }

    BingoStart = (e) => {
        console.log(this.state.get_winning_pattern)
    }

    isValidNumber = ( num, arr = [] ) => {

        if ( !arr.includes( num ) ) {

            return true;

        } else {

            return false;

        }

    };

    generateRandomNumber = ( max, min = 0, arr = [] ) => {

        const _max = Math.floor( max );
        const _min = Math.ceil( min );
        const _arr = arr;
        // random number between and including range
        const _num = Math.floor( Math.random() * ( _max - _min + 1 ) ) + _min;

        // Make sure random number doesn't already exist in the array
        if ( this.isValidNumber( _num, _arr ) ) {

            _arr.push( _num );

            return _num;

        } else {

            // recursive call if invalid
            return this.generateRandomNumber( _max, _min, _arr );

        }

    };

    randomBallSelector = () => {

        const _ballCount  = this.state.ballsArr.length;
        const _randomBall = this.generateRandomNumber( _ballCount - 1 );

        this.setState({BallsArr: _randomBall})

    };

    DrawBall = () => {

        const _ball = this.randomBallSelector();

        console.log(_ball)
    //     const _drawnNumber = parseInt( _ball.split( '-' )[ 1 ] );

    //     if( BINGO.ballCount > 0 ) {

    //         socket.emit('drawBall', _ball);

    //         BINGO.popBall( _ball )
    //              .updateDrawHistory( _ball )
    //              .updateLastDrawn( _ball )
    //              .highlightDrawnBall( _ball )
    //              .highlightCardCell( _drawnNumber );

    //         BINGO.ballCount--;

    //         if( BINGO.ballCount === 0 ) {

    //             // disable draw button
    //             BINGO.domElems.drawButton.disabled = true;
    //             BINGO.domElems.drawButton.classList.add( 'disabled' );
    //             // show the reset button
    //             BINGO.domElems.resetButton.classList.remove( 'display-none' );

    //         }

    //     }

    };

    render() {

        return (
            <>
                <Header />
                <div id="bingo-body" className="container-fluid bg-dark">
                    <div className="row">
                        <div className="col-xl-4">
                            <div className="row">
                                <div className="col-xl-12">
                                    <h2>Last Ball: <span id="js-last-num" className="last-num">Click to Draw</span></h2>
                                    <p>Draw History</p>
                                    <ol id="js-history"></ol>
                                    <button className="btn btn-dark btnsm btn-block" onClick={this.DrawBall}>Draw Number</button>
                                    <button className="btn btn-dark btnsm btn-block" onClick={this.BingoStart}>Start Game</button>
                                    <button className="btn btn-dark btnsm btn-block" onClick={this.GetPattern}>Set Winning Pattern</button>
                                </div>
                            </div>
                        </div>
                        <div className="col-xl-4">
                            WINNING PATTERNS
                            <table id="winning-pattern-card" data-set="false">
                                <tbody>
                                    <tr>
                                        <td className="pattern" onClick={this.Setppattern} data-cell="B1"></td>
                                        <td className="pattern" onClick={this.Setppattern} data-cell="I1"></td>
                                        <td className="pattern" onClick={this.Setppattern} data-cell="N1"></td>
                                        <td className="pattern" onClick={this.Setppattern} data-cell="G1"></td>
                                        <td className="pattern" onClick={this.Setppattern} data-cell="O1"></td>
                                    </tr>
                                    <tr>
                                        <td className="pattern" onClick={this.Setppattern} data-cell="B2"></td>
                                        <td className="pattern" onClick={this.Setppattern} data-cell="I2"></td>
                                        <td className="pattern" onClick={this.Setppattern} data-cell="N2"></td>
                                        <td className="pattern" onClick={this.Setppattern} data-cell="G2"></td>
                                        <td className="pattern" onClick={this.Setppattern} data-cell="O2"></td>
                                    </tr>
                                    <tr>
                                        <td className="pattern" onClick={this.Setppattern} data-cell="B3"></td>
                                        <td className="pattern" onClick={this.Setppattern} data-cell="I3"></td>
                                        <td className="pattern marked" data-cell="N3">FREE</td>
                                        <td className="pattern" onClick={this.Setppattern} data-cell="G3"></td>
                                        <td className="pattern" onClick={this.Setppattern} data-cell="O3"></td>
                                    </tr>
                                    <tr>
                                        <td className="pattern" onClick={this.Setppattern} data-cell="B4"></td>
                                        <td className="pattern" onClick={this.Setppattern} data-cell="I4"></td>
                                        <td className="pattern" onClick={this.Setppattern} data-cell="N4"></td>
                                        <td className="pattern" onClick={this.Setppattern} data-cell="G4"></td>
                                        <td className="pattern" onClick={this.Setppattern} data-cell="O4"></td>
                                    </tr>
                                    <tr>
                                        <td className="pattern" onClick={this.Setppattern} data-cell="B5"></td>
                                        <td className="pattern" onClick={this.Setppattern} data-cell="I5"></td>
                                        <td className="pattern" onClick={this.Setppattern} data-cell="N5"></td>
                                        <td className="pattern" onClick={this.Setppattern} data-cell="G5"></td>
                                        <td className="pattern" onClick={this.Setppattern} data-cell="O5"></td>
                                    </tr>
                                </tbody>
                            </table>
                        </div>
                        <div className="col-xl-4">

                        </div>
                    </div>
                </div>
            </>
        );
    }

}

export default Game;
