import React, { Component } from 'react';
import axios from 'axios';

class header extends Component {

    state = {
        name: '',
        listevents: []
    }

    constructor() {
        super();

        this.getUserDetails();
    }

    async componentDidMount() {
        let result = await axios.post(`https://binggo-test.dokyumento.asia/index.php/Binggo/fetch_all_binggo_events`)
        this.setState({listevents: result.data.payload})
        console.log("FETCH LIST OF EVENTS")
        console.log(result)
    }


    getUserDetails = async () => {
        axios.post(`Useraccounts/fetch_user_details_by_user_id`, { user_id: localStorage.user_id })
        .then(res => {

            console.log("FETCH USER DETAILS")
            console.log(res);
            console.log(res.data);

            this.setState({
                name: res.data.payload[0].user_fullname,
            })


        })
        .catch(err => {
            console.log(err)
        });
    }


    render() {
        return (
            <div>
                <nav className="navbar navbar-expand-lg navbar-dark bg-dark">
                    <a className="navbar-brand" href="/">GabayGuro | Online Bingo</a>
                    <button className="navbar-toggler" type="button" data-toggle="collapse" data-target="#navbarNavDropdown" aria-controls="navbarNavDropdown" aria-expanded="false" aria-label="Toggle navigation">
                        <span className="navbar-toggler-icon"></span>
                    </button>
                    <div className="collapse navbar-collapse" id="navbarNavDropdown">
                        <ul className="navbar-nav ml-auto">
                        <li className="nav-item dropdown">
                            <a className="nav-link dropdown-toggle" href="/" id="navbarDropdownMenuLink" role="button" data-toggle="dropdown" aria-haspopup="true" aria-expanded="false">
                                Welcome {this.state.name}
                            </a>
                            <div className="dropdown-menu">
                            </div>
                        </li>
                        </ul>
                    </div>
                </nav>
                <select className="custom-select w-25">
                    <option value={list.binggo_event_id} >{list.binggo_title}</option>
                    {this.state.listevents.length > 0 ? (
                        <>
                            {this.state.listevents.map(list => 
                             <option value={list.binggo_event_id} >{list.binggo_title}</option>
                        )}</>

                    ):(
                       <span></span>
                    )
                    }
                </select>
            </div>
        );
    }
}

export default header;
