import React, { Component } from 'react'
import Header from './header';
import io from 'socket.io-client'
import axios from 'axios';
import './style-admin.css';

var socket = io.connect('http://localhost:4000')
var room = localStorage.room_id
var username = localStorage.name

class Game extends Component {

    state = {
    };

    constructor() {
        super();     

        this.state = {addClass: false}

    }

    toggle() {
        this.setState({addClass: !this.state.addClass});
    }

    componentDidMount() {

        socket.emit('joinRoom', { username, room });


        socket.on('roomUsers', ({ room, users }) => {
            console.log(room)
            console.log(users)
            this.setState({ lop: users })
        });

    }

    Setppattern = (event) => {
        event.target.className =""
    }



    render() {

        let boxClass = ["pattern"];
        if(this.state.addClass) {
        boxClass.push('marked');
        }

        return (
            <>
                <Header />
                <div id="bingo-body" className="container-fluid bg-dark">
                    <div className="row">
                        <div className="col-xl-4">
                            <div className="row">
                                <div className="col-xl-12">
                                    <button className="btn btn-dark btnsm btn-block">Draw Number</button>
                                    <button className="btn btn-dark btnsm btn-block">Start Game</button>
                                    <button className="btn btn-dark btnsm btn-block">Set Winning Pattern</button>
                                </div>
                            </div>
                        </div>
                        <div className="col-xl-4">
                            WINNING PATTERNS
                            <table id="winning-pattern-card" data-set="false">
                            <tr>
                                <td className="pattern" onClick={this.toggle.bind(this)}  data-cell="B1"></td>
                                <td className="pattern" onClick={this.toggle.bind(this)}  data-cell="I1"></td>
                                <td className="pattern" data-cell="N1"></td>
                                <td className="pattern" data-cell="G1"></td>
                                <td className="pattern" data-cell="O1"></td>
                            </tr>
                            <tr>
                                <td className="pattern" data-cell="B2"></td>
                                <td className="pattern" data-cell="I2"></td>
                                <td className="pattern" data-cell="N2"></td>
                                <td className="pattern" data-cell="G2"></td>
                                <td className="pattern" data-cell="O2"></td>
                            </tr>
                            <tr>
                                <td className="pattern" data-cell="B3"></td>
                                <td className="pattern" data-cell="I3"></td>
                                <td className="marked" data-cell="N3">FREE</td>
                                <td className="pattern" data-cell="G3"></td>
                                <td className="pattern" data-cell="O3"></td>
                            </tr>
                            <tr>
                                <td className="pattern" data-cell="B4"></td>
                                <td className="pattern" data-cell="I4"></td>
                                <td className="pattern" data-cell="N4"></td>
                                <td className="pattern" data-cell="G4"></td>
                                <td className="pattern" data-cell="O4"></td>
                            </tr>
                            <tr>
                                <td className="pattern" data-cell="B5"></td>
                                <td className="pattern" data-cell="I5"></td>
                                <td className="pattern" data-cell="N5"></td>
                                <td className="pattern" data-cell="G5"></td>
                                <td className="pattern" data-cell="O5"></td>
                            </tr>
                            </table>
                        </div>
                        <div className="col-xl-4">

                        </div>
                    </div>
                </div>
            </>
        );
    }

}

export default Game;
