import React, { Component } from 'react';
import axios from 'axios';

class header extends Component {

    state = {
        name: '',
        bingoevents: []
    }

    constructor() {
        super();

        this.getUserDetails();
    }

    getBingoEvents = async () => {

        let data = await axios.get(`Binggo/fetch_all_binggo_events`)
        .then(({data}) => data);

        this.setState({ bingoevents: data });
    }

    getUserDetails = async () => {
        axios.post(`Useraccounts/fetch_user_details_by_user_id`, { user_id: localStorage.user_id })
        .then(res => {

            console.log("FETCH USER DETAILS")
            console.log(res);
            console.log(res.data);

            this.setState({
                name: res.data.payload[0].user_fullname,
            })

            this.getBingoEvents();


        })
        .catch(err => {
            console.log(err)
        });
    }


    render() {
        return (
            <>
                <nav className="navbar navbar-expand-lg navbar-dark bg-dark">
                    <a className="navbar-brand" href="/">GabayGuro | Online Bingo</a>
                    <button className="navbar-toggler" type="button" data-toggle="collapse" data-target="#navbarNavDropdown" aria-controls="navbarNavDropdown" aria-expanded="false" aria-label="Toggle navigation">
                        <span className="navbar-toggler-icon"></span>
                    </button>
                    <div className="collapse navbar-collapse" id="navbarNavDropdown">
                        <ul className="navbar-nav ml-auto">
                        {/* <li className="nav-item active">
                            <a className="nav-link" href="/">Home <span className="sr-only">(current)</span></a>
                        </li>
                        <li className="nav-item">
                            <a className="nav-link" href="/">Features</a>
                        </li>
                        <li className="nav-item">
                            <a className="nav-link" href="/">Pricing</a>
                        </li> */}
                        <li className="nav-item dropdown">
                            <a className="nav-link dropdown-toggle" href="/" id="navbarDropdownMenuLink" role="button" data-toggle="dropdown" aria-haspopup="true" aria-expanded="false">
                                Welcome {this.state.name}
                            </a>
                            <div className="dropdown-menu">
                                <a className="dropdown-item" href="/">Logout</a>
                            </div>
                        </li>
                        </ul>
                    </div>
                </nav>
                <select className="custom-select w-25">
                    <option defaultValue>Select an event</option>
                    {this.state.bingoevents.map(bingoevent => 
                        <option>{bingoevent.binggo_title}</option>    
                    )}
                </select>
            </>
        );
    }
}

export default header;
